mapper.select:
select

from kpi_fixed


       ( user_code regexp #{idOrName}
            or user_name regexp #{idOrName})


        and date_format(attendance_month, '%Y-%m') = #{toMonth}

select dj.job_gs, dj.work_gs, dj.job_ratio, dj.job_type, CAST( TRUNCATE(CAST(ka.attendance_day AS DECIMAL) / CAST(ka.month_day AS DECIMAL(5, 2) ), 6 ) AS DECIMAL(9, 6) ) AS percentage FROM dict_user du LEFT JOIN kpi_attendance ka ON ka.user_code = du.user_code LEFT JOIN dict_job dj ON dj.j_id = du.j_id
du.user_code = #{userCode}
and date_format(ka.attendance_month, '%Y-%m') = date_format(#{attendanceMonth}, '%Y-%m')
